    //     }

    //     setProducts(loadedProducts);
    //   };
    //     fetchProducts().catch((error) => {
    //   });
    //     console.log('Test');




    const productItem = product.find((prd) => prd.id === params.productId);

    if (!product) {
        return <p>No product found!</p>;
    }

    return (
        <Card>
            <section>
                <h3>Product Detail</h3>
                <ul>
                <ProductItem
                    key={product.id}
                    id={product.id}
                    name={product.name}
                    // description={product.description}
                    price={product.price}
                    /> 
                </ul>

                <Link className='btn' to={`/products/${props.id}`}>
                    View Detail
                </Link>
            </section>
        </Card>
  );
};

export default ProductDetail;
