import ProductItem from './ProductItem';
import { useEffect, useState } from 'react';

import classes from './Products.module.css';

const Products = (props) => {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch(
        'https://react-http-838c9-default-rtdb.firebaseio.com/products.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedProducts = [];

      for (const key in responseData) {
        loadedProducts.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      console.log(loadedProducts);

      setProducts(loadedProducts);
      setIsLoading(false);
    };

      fetchProducts().catch((error) => {
      setIsLoading(false);
    });
    console.log('Test');
  }, []);

  if (isLoading) {
    return (
      <section className={classes.loading}>
        <p>Loading...</p>
      </section>
    );
  }

  // Get all producs list from API
  const productsList = products.map((product) => (
    <ProductItem
      key={product.id}
      id={product.id}
      name={product.name}
      description={product.description}
      price={product.price}
    />
  ));

  // Filtered Category from API
  // let filteredProducts = [];
  // const filteredCategoryHandler = (product) => {
  //   for (const key in loadedProducts){
  //     if(loadedProducts.category)
  //   }
  // };

  const searchHandler = (event) => {
    console.log(event.target.value);
    return event.target.value;
  };


  return (
    <section className={classes.products}>
      <h2>Buy your favorite products</h2>
      <div className={classes.category}>
        <label>Choose category: </label>
          <select name="product" id="product">
            <option value="candy">Candy</option>
            <option value="cake">Cake</option>
          </select>
      </div>
      <ul>
        {productsList}
      </ul>
    </section>
  );
};

export default Products;
