import Products from "./components/Product/Products";
import Header from "./components/Layout/Header"
import SearchBox from "./components/Product/SearchBox";
import {useState, useEffect} from 'react';

function App() {

//------------------CALL API---------------------

  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch(
        'https://react-http-838c9-default-rtdb.firebaseio.com/products.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedProducts = [];

      for (const key in responseData) {
        loadedProducts.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      console.log(loadedProducts);

      setProducts(loadedProducts);
      setIsLoading(false);
    };

      fetchProducts().catch((error) => {
      setIsLoading(false);
    });
    console.log('Test');
  }, []);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }
//----------------FUNCTIONS-----------------------

  inputSearchHandler = (event) => {
    return ({searchProduct: event.target.value});
  };


  let filteredProduct = products.filter((product => {
    return product.name.toLowerCase().includes(searchProduct.toLowerCase())
  }));

  return (

    <main>
      <Header/>
      <SearchBox inputSearch={inputSearchHandler}/>
      {/* <Products products={products}/> */}
      <Products products={filteredProduct}/>
    </main>
  );
}
// import { useState } from "react";

export default App;
