import { useEffect, useState } from 'react';

// Pagination: react-pagination
import 'react-pagination-bar/dist/index.css'
// import { Pagination } from "react-pagination-bar"

import ProductItem from './ProductItem';
import classes from './Products.module.css';

const Products = (props) => {
  const { input } = props;

  const [products, setProducts] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  // Filter category
  const [category, setCategory] = useState(products.category);

  const [currentPage, setCurrentPage] = useState(1);
  const pageProductLimit = 10;

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch(
        'https://react-http-838c9-default-rtdb.firebaseio.com/products.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedProducts = [];

      for (const key in responseData) {
        loadedProducts.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
          category: responseData[key].category,
        });
      }

      setProducts(loadedProducts);
    };
      fetchProducts().catch((error) => {
    });
  }, [input, currentPage, category]);

 // Filter data function
  useEffect(() => {
    const searchedData = products.filter((el) => {
      if (input === '') {
          return el;
      } else {
          return el.name.toLowerCase().includes(input);
      }
  })
  setFilteredData(searchedData);
  },[products, input, currentPage]);

  const totalPages = Math.ceil(products.length / pageProductLimit); 
  const handlePageChange = (newPage) => {
    // console.log(newPage);
    setCurrentPage(newPage);
  };

  // State của Dropdownlist
  const [selected, setSelected] = useState(products[0].categories[0]);
  // State của list  category from API
  const [selectedCategory, setSelectedCategory] = useState(data);

  useEffect(() => {
    const categories = products.filter((item) => item.category[0] === selected)
    setSelectedCategory(categories);
  }, [products, selected]);

  const handleSelect = (e) => {
    setSelected(e.target.value);
  };

  return (
    <section className={classes.products}>
      <h2>Buy your favorite products</h2>

      <select onChange={handleSelect}>
        {data.map((item) =>
            item.categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))
          )}
      </select>

      <ul>
        {filteredData.slice(
          (currentPage - 1) * pageProductLimit,
          (currentPage - 1) * pageProductLimit + pageProductLimit
        ).map((product) => (
          <ProductItem
            key={product.id}
            id={product.id}
            name={product.name}
            description={product.description}
            price={product.price}
            /> 
        ))}
      </ul>

      <div className={classes.paginationBtn}>
          <button
              // Disable button prev khi là trang số 1 (đầu tiên)
              disabled = {currentPage <= 1}
              onClick = {() => handlePageChange(currentPage - 1)}
          >
          Prev
          </button>
          <button
              // Disable button next khi là trang số 1 (đầu tiên)
              disabled = {currentPage === totalPages}
              onClick = {() => handlePageChange(currentPage + 1)}
          >
          Next
          </button>
      </div>
      <br/>

    </section>
  );
};

export default Products;
