import Products from "./components/Product/Products";
import Header from "./components/Layout/Header"
import SearchBox from "./components/Product/SearchBox";
import {useState, useEffect} from 'react';

function App() {

//------------------CALL API---------------------

  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [inputText, setInputText] = useState(""); // SearchBox state

  // const [enteredValue, setEnteredValue] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch(
        'https://react-http-838c9-default-rtdb.firebaseio.com/products.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedProducts = [];

      for (const key in responseData) {
        loadedProducts.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      console.log(loadedProducts);

      setProducts(loadedProducts);
      setIsLoading(false);
    };

      fetchProducts().catch((error) => {
      setIsLoading(false);
    });
    console.log('Test');
  }, []);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }
//----------------FUNCTIONS-----------------------
let inputHandler = (e) => {
  //convert input text to lower case
  var lowerCase = e.target.value.toLowerCase();
  setInputText(lowerCase);
};

  // console.log(searchProduct);

  // let filteredProduct = products.filter(product => {
  //   return product.name.toLowerCase().includes(inputSearchHandler.toLowerCase())
  // });

  return (
    <main>
      <Header/>

      <div className="main">
      <h1>React Search</h1>
      <div className="search">
        <input
          id="outlined-basic"
          onChange={inputHandler}
          variant="outlined"
          fullWidth
          label="Search"
        />
      </div>
      {/* <Products input={inputText} /> */}
    </div>

      {/* <Products products={products}/> */}
    </main>
  );
}
// import { useState } from "react";

export default App;
